import React, { useState } from 'react';

const Checkout = ({ carrito, productos }) => {
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');

  const handleCheckout = () => {
    const cartDetails = productos
      .filter(producto => carrito[producto.id] > 0)
      .map(producto => `${producto.nombre} x ${carrito[producto.id]}`)
      .join(', ');
    const mensaje = `https://wa.me/numero?text=Hola,%20mi%20pedido%20es:%20${cartDetails}%20y%20mi%20nombre%20es:%20${nombre}%20${apellido}`;
    window.location.href = mensaje;
  };

  return (
    <div>
      <h2>Checkout</h2>
      <input
        type="text"
        placeholder="Nombre"
        value={nombre}
        onChange={(e) => setNombre(e.target.value)}
      />
      <input
        type="text"
        placeholder="Apellido"
        value={apellido}
        onChange={(e) => setApellido(e.target.value)}
      />
      <button onClick={handleCheckout}>Realizar Pedido</button>
    </div>
  );
};

export default Checkout;
