import React, { useState } from 'react';

const Contact = () => {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [enviado, setEnviado] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí podrías integrar una API de correo electrónico para enviar el mensaje
    console.log('Nombre:', nombre);
    console.log('Email:', email);
    console.log('Mensaje:', mensaje);
    setEnviado(true);

    // Limpiar el formulario
    setNombre('');
    setEmail('');
    setMensaje('');
  };

  return (
    <div style={styles.container}>
      <h2>Contacto</h2>
      <p>Si tienes alguna consulta o deseas más información, contáctanos mediante el siguiente formulario:</p>

      {enviado && <p style={styles.successMessage}>¡Gracias! Tu mensaje ha sido enviado.</p>}

      <form onSubmit={handleSubmit} style={styles.form}>
        <input
          type="text"
          placeholder="Nombre"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
          style={styles.input}
          required
        />
        <input
          type="email"
          placeholder="Correo Electrónico"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={styles.input}
          required
        />
        <textarea
          placeholder="Mensaje"
          value={mensaje}
          onChange={(e) => setMensaje(e.target.value)}
          style={styles.textarea}
          required
        ></textarea>
        <button type="submit" style={styles.button}>Enviar</button>
      </form>

      <h3>Información de Contacto</h3>
      <p>Teléfono: +595 123 456 789</p>
      <p>Email: contacto@donyum.com</p>
      <p>Dirección: Calle Ejemplo 123, Ciudad, País</p>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    maxWidth: '600px',
    margin: '0 auto',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
  },
  input: {
    padding: '10px',
    marginBottom: '10px',
    borderRadius: '5px',
    border: '1px solid #ddd',
  },
  textarea: {
    padding: '10px',
    marginBottom: '10px',
    borderRadius: '5px',
    border: '1px solid #ddd',
    minHeight: '100px',
  },
  button: {
    padding: '10px',
    backgroundColor: '#333',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  successMessage: {
    color: 'green',
    marginBottom: '10px',
  },
};

export default Contact;
