import React from 'react';
import { Link } from 'react-router-dom';
import productos from '../data/productos.json'; // Importa el archivo de productos

const Cart = ({ carrito, setCarrito }) => {
  const handleRemoveItem = (id) => {
    setCarrito((prev) => {
      const updatedCart = { ...prev };
      delete updatedCart[id];
      return updatedCart;
    });
  };

  const total = Object.entries(carrito).reduce((sum, [id, cantidad]) => {
    const producto = productos.find((item) => item.id === parseInt(id));
    return sum + (producto.precio * cantidad);
  }, 0);

  return (
    <div>
      <h2>Carrito</h2>
      <div>
        {Object.entries(carrito).map(([id, cantidad]) => {
          const producto = productos.find((item) => item.id === parseInt(id));
          return (
            <div key={id}>
              <h3>{producto.nombre}</h3>
              <p>Precio: {producto.precio} Gs</p>
              <p>Cantidad: {cantidad}</p>
              <button onClick={() => handleRemoveItem(id)}>Eliminar</button>
            </div>
          );
        })}
      </div>
      <h3>Total: {total.toLocaleString()} Gs</h3>
      <Link to="/checkout">
        <button>Pagar</button>
      </Link>
    </div>
  );
};

export default Cart;
