import React, { useState } from 'react'; // Asegúrate de importar useState
import Navbar from './components/Navbar';
import ProductList from './components/ProductList';
import Checkout from './components/Checkout';
import productos from './data/productos.json'; // Importa el archivo de productos

function App() {
  const [carrito, setCarrito] = useState({});
  const [mostrarCheckout, setMostrarCheckout] = useState(false);

  const handleCheckoutClick = () => {
    if (Object.values(carrito).some(cantidad => cantidad > 0)) {
      setMostrarCheckout(true);
    } else {
      alert("Aún no tienes productos en el carrito.");
    }
  };

  return (
    <div className="App">
      <Navbar />
      <ProductList carrito={carrito} setCarrito={setCarrito} />
      <button onClick={handleCheckoutClick}>Checkout</button>
      {mostrarCheckout && <Checkout carrito={carrito} productos={productos} />}
    </div>
  );
}

export default App;
