import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.jpg';

const Navbar = ({ carrito }) => {
  const [menuOpen, setMenuOpen] = useState(false);
  const totalItems = Object.values(carrito).reduce((sum, cantidad) => sum + cantidad, 0);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <nav style={styles.nav}>
      <div style={styles.logoContainer}>
        <Link to="/" style={styles.logoLink}>
          <img src={logo} alt="Don Yum" style={styles.logo} />
        </Link>
      </div>

      {/* Botón de hamburguesa solo en pantallas pequeñas */}
      <div style={styles.hamburger} onClick={toggleMenu}>
        ☰
      </div>

      {/* Enlaces de navegación, visibles según el estado de pantalla */}
      <div style={{ ...styles.links, ...(menuOpen ? styles.showMenu : styles.hideMenu) }}>
        <Link to="/" style={styles.link} onClick={() => setMenuOpen(false)}>Inicio</Link>
        <Link to="/about" style={styles.link} onClick={() => setMenuOpen(false)}>Sobre Nosotros</Link>
        <Link to="/contact" style={styles.link} onClick={() => setMenuOpen(false)}>Contacto</Link>
      </div>

      {/* Ícono del carrito */}
      <Link to="/cart" style={styles.cart}>
        🛒 {totalItems > 0 ? `(${totalItems})` : ""}
      </Link>
    </nav>
  );
};

const styles = {
  nav: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '15px 30px',
    background: 'rgba(51, 51, 51, 0.5)',
    backdropFilter: 'blur(10px)',
    borderRadius: '10px',
    boxShadow: '0px 4px 12px rgba(0, 0, 0, 0.2)',
    color: '#fff',
    margin: '10px 20px',
    position: 'relative',
  },
  logoContainer: {
    display: 'flex',
    alignItems: 'center',
  },
  logoLink: {
    textDecoration: 'none',
  },
  logo: {
    height: '50px',
    borderRadius: '10px',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.2)',
  },
  hamburger: {
    display: 'none', // Escondido por defecto; visible solo en pantallas pequeñas
    fontSize: '24px',
    cursor: 'pointer',
  },
  links: {
    display: 'flex',
    gap: '20px',
  },
  hideMenu: {
    display: 'none',
  },
  showMenu: {
    display: 'flex',
    flexDirection: 'column',
    position: 'absolute',
    top: '70px',
    right: '30px',
    background: 'rgba(51, 51, 51, 0.8)',
    borderRadius: '10px',
    padding: '10px',
    boxShadow: '0px 4px 12px rgba(0, 0, 0, 0.3)',
  },
  link: {
    color: '#ccc',
    textDecoration: 'none',
    fontSize: '18px',
    fontWeight: '500',
    padding: '10px 15px',
    borderRadius: '8px',
    transition: '0.3s',
    background: 'rgba(255, 255, 255, 0.1)',
    backdropFilter: 'blur(5px)',
  },
  cart: {
    fontSize: '20px',
    color: '#fff',
    textDecoration: 'none',
    padding: '10px 15px',
    borderRadius: '8px',
    background: 'rgba(255, 255, 255, 0.1)',
    backdropFilter: 'blur(5px)',
    transition: '0.3s',
  },
  '@media (max-width: 768px)': {
    links: {
      display: 'none', // Escondido en pantallas pequeñas
    },
    hamburger: {
      display: 'block', // Mostrar el botón de hamburguesa en pantallas pequeñas
    },
  },
};

export default Navbar;
