import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import logo from '../assets/logo.jpg';

const Navbar = ({ carrito }) => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
  const location = useLocation(); // Hook para obtener la ruta actual
  const totalItems = Object.values(carrito).reduce((sum, cantidad) => sum + cantidad, 0);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleResize = () => {
    setIsMobile(window.innerWidth <= 768);
    if (window.innerWidth > 768) {
      setMenuOpen(false); // Cerrar menú si se expande a pantalla grande
    }
  };

  useEffect(() => {
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const isActiveLink = (path) => location.pathname === path; // Verificar si el enlace es el activo

  return (
    <nav style={styles.nav}>
      <div style={styles.logoContainer}>
        <Link to="/" style={styles.logoLink}>
          <img src={logo} alt="Don Yum" style={styles.logo} />
        </Link>
      </div>

      {/* Botón de menú hamburguesa para pantallas pequeñas */}
      {isMobile && (
        <button onClick={toggleMenu} style={styles.hamburger}>
          ☰
        </button>
      )}

      {/* Links que se muestran en pantallas grandes y como desplegable en pantallas pequeñas */}
      <div style={{ ...styles.links, display: menuOpen || !isMobile ? 'flex' : 'none' }}>
        <Link to="/" style={isActiveLink('/') ? { ...styles.link, ...styles.activeLink } : styles.link}>Inicio</Link>
        <Link to="/about" style={isActiveLink('/about') ? { ...styles.link, ...styles.activeLink } : styles.link}>Sobre Nosotros</Link>
        <Link to="/contact" style={isActiveLink('/contact') ? { ...styles.link, ...styles.activeLink } : styles.link}>Contacto</Link>
      </div>

      <Link to="/cart" style={styles.cart}>
        🛒 {totalItems > 0 ? `(${totalItems})` : ""}
      </Link>
    </nav>
  );
};

const styles = {
  nav: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '10px 20px',
    background: 'rgba(51, 51, 51, 0.8)',
    backdropFilter: 'blur(10px)',
    borderRadius: '10px',
    color: '#fff',
  },
  logoContainer: {
    display: 'flex',
    alignItems: 'center',
  },
  logoLink: {
    textDecoration: 'none',
  },
  logo: {
    height: '40px',
    borderRadius: '8px',
  },
  links: {
    display: 'flex',
    gap: '15px',
    flexDirection: 'row',
  },
  link: {
    color: '#ccc',
    textDecoration: 'none',
    fontSize: '16px',
    fontWeight: '500',
    padding: '8px 12px',
    borderRadius: '8px',
    transition: '0.3s',
    background: 'rgba(255, 255, 255, 0.1)',
    backdropFilter: 'blur(5px)',
  },
  activeLink: {
    background: 'rgba(255, 255, 255, 0.3)', // Estilo resaltado para el enlace activo
    color: '#fff',
  },
  cart: {
    fontSize: '18px',
    color: '#fff',
    textDecoration: 'none',
    padding: '8px 12px',
    borderRadius: '8px',
    background: 'rgba(255, 255, 255, 0.1)',
    backdropFilter: 'blur(5px)',
  },
  hamburger: {
    fontSize: '24px',
    color: '#fff',
    background: 'none',
    border: 'none',
    cursor: 'pointer',
    display: 'none',
  },
};

// Media query para el estilo de menú en pantallas pequeñas
const mediaStyles = `
@media (max-width: 768px) {
  .hamburger {
    display: block; /* Mostrar botón hamburguesa en pantallas pequeñas */
  }
  .links {
    display: none; /* Ocultar enlaces en pantallas pequeñas por defecto */
    flex-direction: column; /* Configuración para el menú desplegable */
    gap: 10px;
    position: absolute;
    top: 60px;
    right: 10px;
    background: rgba(51, 51, 51, 0.95);
    padding: 15px;
    border-radius: 8px;
    box-shadow: 0px 4px 12px rgba(0, 0, 0, 0.3);
    z-index: 1000;
  }
}
`;

const styleSheet = document.createElement("style");
styleSheet.type = "text/css";
styleSheet.innerText = mediaStyles;
document.head.appendChild(styleSheet);

export default Navbar;
