import React, { useState } from 'react';

const Checkout = ({ carrito, productos }) => {
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');

  const handleConfirm = () => {
    const cartDetails = Object.entries(carrito)
      .map(([id, cantidad]) => {
        const producto = productos.find((item) => item.id === parseInt(id));
        return `${producto.nombre} x ${cantidad}`;
      })
      .join(', ');

    const mensaje = `https://wa.me/numero?text=Hola,%20mi%20pedido%20es:%20${cartDetails}%20y%20mi%20nombre%20es:%20${nombre}%20${apellido}`;
    window.location.href = mensaje;
  };

  return (
    <div>
      <h2>Detalles del Pedido</h2>
      {Object.entries(carrito).map(([id, cantidad]) => {
        const producto = productos.find((item) => item.id === parseInt(id));
        return (
          <div key={id}>
            <h3>{producto.nombre}</h3>
            <p>{cantidad} x {producto.precio.toLocaleString()} Gs</p>
          </div>
        );
      })}
      <input
        type="text"
        placeholder="Nombre"
        value={nombre}
        onChange={(e) => setNombre(e.target.value)}
      />
      <input
        type="text"
        placeholder="Apellido"
        value={apellido}
        onChange={(e) => setApellido(e.target.value)}
      />
      <button onClick={handleConfirm}>Confirmar</button>
    </div>
  );
};

export default Checkout;
