import React, { useState } from 'react';

// Datos de ejemplo de productos
const productos = [
  { id: 1, nombre: "Brownie", descripcion: "Delicioso brownie de chocolate", precio: 10000, categoria: "Postres" },
  { id: 2, nombre: "Empanaditas", descripcion: "Empanadas rellenas de carne", precio: 5000, categoria: "Bocaditos" },
  // Agrega más productos según necesites
];

const ProductList = () => {
  const [carrito, setCarrito] = useState({});
  const [filtro, setFiltro] = useState("Todos");

  const agregarAlCarrito = (producto) => {
    setCarrito((prev) => ({
      ...prev,
      [producto.id]: (prev[producto.id] || 0) + 1,
    }));
  };

  const quitarDelCarrito = (producto) => {
    setCarrito((prev) => {
      const nuevoCarrito = { ...prev };
      if (nuevoCarrito[producto.id] > 0) nuevoCarrito[producto.id] -= 1;
      return nuevoCarrito;
    });
  };

  const filtrarProductos = filtro === "Todos" ? productos : productos.filter((p) => p.categoria === filtro);

  return (
    <div style={styles.container}>
      <h2>Productos y Servicios</h2>

      {/* Filtros de categoría */}
      <div style={styles.filtros}>
        <button onClick={() => setFiltro("Todos")}>Todos</button>
        <button onClick={() => setFiltro("Postres")}>Postres</button>
        <button onClick={() => setFiltro("Bocaditos")}>Bocaditos</button>
        {/* Agrega botones para otras categorías */}
      </div>

      {/* Lista de productos */}
      <div style={styles.productList}>
        {filtrarProductos.map((producto) => (
          <div key={producto.id} style={styles.producto}>
            <h3>{producto.nombre}</h3>
            <p>{producto.descripcion}</p>
            <p>Precio: {producto.precio} G</p>
            <div style={styles.cantidad}>
              <button onClick={() => quitarDelCarrito(producto)}>-</button>
              <span>{carrito[producto.id] || 0}</span>
              <button onClick={() => agregarAlCarrito(producto)}>+</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
  },
  filtros: {
    marginBottom: '20px',
  },
  productList: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '20px',
  },
  producto: {
    border: '1px solid #ddd',
    padding: '10px',
    borderRadius: '5px',
    width: '200px',
  },
  cantidad: {
    display: 'flex',
    alignItems: 'center',
    gap: '10px',
  },
};

export default ProductList;
