import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.jpg';

const Navbar = ({ carrito }) => {
  const totalItems = Object.values(carrito).reduce((sum, cantidad) => sum + cantidad, 0);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);

  return (
    <nav style={styles.nav}>
      <div style={styles.logoContainer}>
        <Link to="/" style={styles.logoLink}>
          <img src={logo} alt="Don Yum" style={styles.logo} />
        </Link>
      </div>

      {/* Icono del men칰 en pantallas peque침as */}
      <div style={styles.hamburger} onClick={toggleMenu}>
        驕
      </div>

      {/* Links de navegaci칩n */}
      <div style={{ ...styles.links, display: isMenuOpen ? 'flex' : 'none' }}>
        <Link to="/" style={styles.link} onClick={toggleMenu}>Inicio</Link>
        <Link to="/about" style={styles.link} onClick={toggleMenu}>Sobre Nosotros</Link>
        <Link to="/contact" style={styles.link} onClick={toggleMenu}>Contacto</Link>
        <Link to="/cart" style={styles.cart} onClick={toggleMenu}>
          游 {totalItems > 0 ? `(${totalItems})` : ""}
        </Link>
      </div>
    </nav>
  );
};

const styles = {
  nav: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '15px 20px',
    background: 'rgba(51, 51, 51, 0.5)',
    backdropFilter: 'blur(10px)',
    borderRadius: '10px',
    boxShadow: '0px 4px 12px rgba(0, 0, 0, 0.2)',
    color: '#fff',
  },
  logoContainer: {
    display: 'flex',
    alignItems: 'center',
  },
  logoLink: {
    textDecoration: 'none',
  },
  logo: {
    height: '50px',
    borderRadius: '10px',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.2)',
  },
  links: {
    display: 'flex',
    gap: '20px',
    alignItems: 'center',
    flexDirection: 'row',
  },
  link: {
    color: '#ccc',
    textDecoration: 'none',
    fontSize: '18px',
    fontWeight: '500',
    padding: '10px 15px',
    borderRadius: '8px',
    transition: '0.3s',
    background: 'rgba(255, 255, 255, 0.1)',
    backdropFilter: 'blur(5px)',
  },
  cart: {
    fontSize: '20px',
    color: '#fff',
    textDecoration: 'none',
    padding: '10px 15px',
    borderRadius: '8px',
    background: 'rgba(255, 255, 255, 0.1)',
    backdropFilter: 'blur(5px)',
    transition: '0.3s',
  },
  hamburger: {
    fontSize: '24px',
    color: '#fff',
    display: 'none', // Oculto en pantallas grandes
    cursor: 'pointer',
  },
  // Media query en CSS para hacer el men칰 responsive
  '@media (max-width: 768px)': {
    links: {
      position: 'absolute',
      top: '60px',
      left: '0',
      right: '0',
      background: 'rgba(51, 51, 51, 0.9)',
      flexDirection: 'column',
      gap: '10px',
      padding: '10px',
      borderRadius: '8px',
      boxShadow: '0px 4px 12px rgba(0, 0, 0, 0.2)',
    },
    hamburger: {
      display: 'block', // Muestra el 칤cono de men칰 en pantallas peque침as
    },
  },
};

export default Navbar;
