// src/components/Navbar.js
import React from 'react';
import logo from '../assets/logo.jpg';

const Navbar = ({ carrito, handleCheckoutClick }) => {
  // Contar la cantidad total de productos en el carrito
  const totalItems = Object.values(carrito).reduce((sum, cantidad) => sum + cantidad, 0);

  return (
    <nav style={styles.nav}>
      <div style={styles.logoContainer}>
        <img src={logo} alt="Don Yum" style={styles.logo} />
      </div>
      <div style={styles.links}>
        <a href="#inicio" style={styles.link}>Inicio</a>
        <a href="#sobre-nosotros" style={styles.link}>Sobre Nosotros</a>
        <a href="#contacto" style={styles.link}>Contacto</a>
      </div>
      <div style={styles.cart} onClick={handleCheckoutClick}>
        ðŸ›’ {totalItems > 0 ? `(${totalItems})` : ""}
      </div>
    </nav>
  );
};

const styles = {
  nav: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '10px 20px',
    backgroundColor: '#333', // tono gris oscuro
    color: '#fff',
  },
  logoContainer: {
    display: 'flex',
    alignItems: 'center',
  },
  logo: {
    height: '50px',
  },
  links: {
    display: 'flex',
    gap: '15px',
  },
  link: {
    color: '#ccc', // tono gris claro
    textDecoration: 'none',
    fontSize: '16px',
  },
  cart: {
    fontSize: '20px',
    cursor: 'pointer',
  },
};

export default Navbar;
