import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.jpg';

const Navbar = ({ carrito }) => {
  const [menuOpen, setMenuOpen] = useState(false);
  const totalItems = Object.values(carrito).reduce((sum, cantidad) => sum + cantidad, 0);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <nav style={styles.nav}>
      <div style={styles.logoContainer}>
        {/* Logo clickeable que redirige a la página de inicio */}
        <Link to="/" style={styles.logoLink}>
          <img src={logo} alt="Don Yum" style={styles.logo} />
        </Link>
      </div>

      {/* Botón de menú hamburguesa */}
      <button onClick={toggleMenu} style={styles.hamburger}>
        ☰
      </button>

      {/* Links que se ocultan en pantallas pequeñas */}
      <div style={{ ...styles.links, display: menuOpen || window.innerWidth > 768 ? 'flex' : 'none' }}>
        <Link to="/" style={styles.link}>Inicio</Link>
        <Link to="/about" style={styles.link}>Sobre Nosotros</Link>
        <Link to="/contact" style={styles.link}>Contacto</Link>
      </div>

      <Link to="/cart" style={styles.cart}>
        🛒 {totalItems > 0 ? `(${totalItems})` : ""}
      </Link>
    </nav>
  );
};

const styles = {
  nav: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '15px 30px',
    background: 'rgba(51, 51, 51, 0.5)',
    backdropFilter: 'blur(10px)',
    borderRadius: '10px',
    boxShadow: '0px 4px 12px rgba(0, 0, 0, 0.2)',
    color: '#fff',
    margin: '10px 20px',
  },
  logoContainer: {
    display: 'flex',
    alignItems: 'center',
  },
  logoLink: {
    textDecoration: 'none',
  },
  logo: {
    height: '50px',
    borderRadius: '10px',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.2)',
  },
  links: {
    display: 'flex',
    gap: '20px',
  },
  link: {
    color: '#ccc',
    textDecoration: 'none',
    fontSize: '18px',
    fontWeight: '500',
    padding: '10px 15px',
    borderRadius: '8px',
    transition: '0.3s',
    background: 'rgba(255, 255, 255, 0.1)',
    backdropFilter: 'blur(5px)',
  },
  cart: {
    fontSize: '20px',
    color: '#fff',
    textDecoration: 'none',
    padding: '10px 15px',
    borderRadius: '8px',
    background: 'rgba(255, 255, 255, 0.1)',
    backdropFilter: 'blur(5px)',
    transition: '0.3s',
  },
  hamburger: {
    fontSize: '24px',
    color: '#fff',
    background: 'none',
    border: 'none',
    cursor: 'pointer',
    display: 'none',
  },
};

// Media query para el estilo de menú en pantallas pequeñas
const mediaStyles = `
@media (max-width: 768px) {
  .hamburger {
    display: block; /* Mostrar botón hamburguesa en pantallas pequeñas */
  }
  .links {
    display: none; /* Ocultar enlaces en pantallas pequeñas por defecto */
    flex-direction: column; /* Configuración para el menú desplegable */
    gap: 10px;
    position: absolute;
    top: 70px; /* Ajustar según la altura del navbar */
    right: 30px;
    background: rgba(51, 51, 51, 0.9);
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 4px 12px rgba(0, 0, 0, 0.3);
    z-index: 1000;
  }
}
`;

// Agregar mediaStyles como una hoja de estilo en el documento
const styleSheet = document.createElement("style");
styleSheet.type = "text/css";
styleSheet.innerText = mediaStyles;
document.head.appendChild(styleSheet);

export default Navbar;
